INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (2364, 163, 10, 6, '2023-04-15', 116, 'Titular',null, '2', 'Partido muy displicente. no demostro actitud para jugar. Entro confiado. Se equivoco en los pases, en sus acciones tacticas. Bajo mucho si nivel.');
