INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3225, 239, 14, 10, '2023-06-24', 163, 'Suplente ingresa',null, '2', 'Regular partido. Ingresó con bastante intensidad y ayudando en labores defensivas de forma constante pero en las acciones ofensivas no pudo entrar mucho en juego y cuando lo hizo la mayoría de sus acciones jugó hacia atrás, generando que el equipo perdiera profundidad y progresión.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3226, 239, 14, 10, '2023-06-24', 182, 'Titular',null, '4', 'Muy buen partido. Fue agresivo cada vez que tuvo sus duelos defensivos contra el extremo de su banda, ganando la mayoría de ellos. En ofensiva sus asociaciones y proyecciones al ataque fueron bastante buenas, generando mucho riesgo junto al extremo por su banda. Ejecutó de buena forma táctica fija.');
