INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3286, 241, 14, 10, '2023-08-03', 172, 'Titular',null, '5', 'Excelente partido. Fue el jugador más desequilibrante en zonas ofensivas, ganando la mayoría de sus duelos ofensivos, insistiendo de forma constante y generando ocasiones permanentemente. Marcó 3 goles bastante buenos. En labores defensivas ayudó mucho al lateral por su banda durante todo el encuentro.');
