INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3317, 243, 14, 10, '2023-08-05', 165, 'Titular',null, '3', 'Buen partido. En labores defensivas estuvo bastante bien, ganando la mayoría de sus duelos defensivos, yendo muy bien al suelo y realizando buenas coberturas a su línea defensiva. En cuando a la fase ofensiva se le vió un tanto impreciso en querer filtrar pases y jugar por los pasillos interiores perdiendo muchas veces la posesión del balón. Pero en general hizo un buen encuentro.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3318, 243, 14, 10, '2023-08-05', 183, 'Suplente ingresa',null, '3', 'Ingresó bien pero un tanto acelerado. Se le vió muy firme en labores defensivas pero en ciertos momentos muy ansioso e impreciso en su accionar por querer resolver de forma rápida. Le permitió al equipo poder tener un respiro en zonas defensivas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3319, 243, 14, 10, '2023-08-05', 166, 'Titular',null, '3', 'Buen partido. Tuvo controlado durante casi la totalidad del encuentro al delantero centro que normalmente se cargaba a su lado, ganando la mayoría de sus duelos defensivos no dejandolo jugar de forma libre. En labores ofensivas le dió buenos inicios al equipo y circuló el balón de buena forma cada vez que el balón pasó por él.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3320, 243, 14, 10, '2023-08-05', 168, 'Lesionado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3321, 243, 14, 10, '2023-08-05', 178, 'Titular',null, '4', 'Muy buen partido. Estuvo firme y agresivo en los duelos defensivos, dificultando de muy buena forma la progresión de los rivales por su banda. En labores ofensivas se proyectó bastante, generando bastante juego ofensivo por el sector derecho.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3322, 243, 14, 10, '2023-08-05', 161, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3323, 243, 14, 10, '2023-08-05', 174, 'Suplente ingresa',null, '3', 'No tuvo muchas intervenciones, pero evitó de forma constante la progresión de los rivales por el sector izquierdo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3324, 243, 14, 10, '2023-08-05', 177, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3325, 243, 14, 10, '2023-08-05', 164, 'Suplente ingresa',null, '2', 'Regular ingreso. Cristian cometió bastantes errores en sus ejecuciones lo que geneeró que el equipo perdiera muchos balones en zona media y cada vez que recibia sus controles no fueron los correctos jugando en reiteradas ocasiones hacia nuestra portería.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3326, 243, 14, 10, '2023-08-05', 180, 'Titular',null, '4', 'Muy buen partido. En el primer y segundo tiempo logró posicionarse de buena forma para siempre recibir libre y poder distribuir el balón al equipo de forma fluida. En labores defensivas fue bastante agresivo, recuperando muchos balones en zona media y evitando ataques rivales constantemente.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3327, 243, 14, 10, '2023-08-05', 297, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3328, 243, 14, 10, '2023-08-05', 176, 'Titular',null, '3', 'Buen partido. A pesar de no ser uno de los mejores partidos de Cristobal lo hizo bastante bien, generando juego ofensivo constantemente, ganando sus duelos ofensivos y asociandose de buena forma. En labores defensivas su rendimiento fue normal, no sobresaliente. No ejecutó bien la táctica fija.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3329, 243, 14, 10, '2023-08-05', 163, 'Suplente ingresa',null, '3', 'Tuvo un buen ingresó buscando permanentemente generar juego ofensivo para el equipo en labores ofensivas. En cuando a loo defensivo estuvo intenso y presionó constantemente, recuperando bastantes balones en zona media.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3330, 243, 14, 10, '2023-08-05', 182, 'Titular',null, '3', 'Buen partido. Primer tiempo muy positivo, ganando la mayoría de los duelos defensivos por su banda y asociandose de buena forma en progresión. En el segundo tiempo su rendimiento bajó considerablemente debido a un error que cometió que significó a posteriori una falta penal.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3331, 243, 14, 10, '2023-08-05', 181, 'Suplente no ingresa',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3332, 243, 14, 10, '2023-08-05', 167, 'Suplente ingresa',null, '3', 'Ingresó bien. Aguantó y descargó muchos balones cada vez que el equipo jugó con él, lo que le permitia a los compañeros poder progresar de mejor manera buscando la portería rival. Tuvo un par de situaciones de gol que no pudo concretar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3333, 243, 14, 10, '2023-08-05', 162, 'Titular',null, '4', 'Muy buen partido. Sin duda fue el jugador más determinante para el equipo. Luchó y generó juego ofensivo constantemente durante todo el partido. Ganó muchos duelos ofensivos, fue agresivo en la recuperación y se logró crear un par de situaciones de gol que no pudo concretar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3334, 243, 14, 10, '2023-08-05', 171, 'Titular',null, '3', 'Buen partido. Buen despliegue en ambas fases, lo que le permitió al equipo estar bastante bien en zona media. Ganó bastantes duelos defensivos en campo rival que fueron aprovechados por el equipo de forma constante.');
