INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3337, 243, 14, 10, '2023-08-05', 184, 'Titular',null, '2', 'Regular partido. No logró ganar sus duelos ofensivos durante la totalidad del encuentro. Se le vió errático y un tanto frustrado en su accionar debido a que no le salían las cosas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3338, 243, 14, 10, '2023-08-05', 172, 'Titular',null, '3', 'Buen partido. En los primeros minutos del partido le costó bastante poder conectar con el jugeeo ofensivo del equipo, no pudiendo ganar sus duelos y cometiendo muchos errores en los pases. Logró marcar un excelente gol trás una jugada individual lo que le permitió al equipo irse en ventaja en el primer tiempo. En labores defensivas colaboró bastante, ayudando a su lateral derecho de forma constante. En el segundo tiempo tras un golpe en su rodilla disminuyó considerablemente su rendimiento.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3339, 243, 14, 10, '2023-08-05', 298, 'No citado',null, null, null);
