INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3341, 244, 14, 10, '2023-08-12', 169, 'Suplente ingresa',null, '2', 'No tuvo muchas intervenciones en labores defensivas pero tiene responsabilidad en el tercer gol dando rebote tras un tiro libre del rival.');
