INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3348, 244, 14, 10, '2023-08-12', 161, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3349, 244, 14, 10, '2023-08-12', 174, 'Suplente ingresa',null, '2', 'No tuvo mucha incidencia en el juego ofensivo ni defensivo del equipo, ya que, el equipo rival no atacó mucho por su banda en los últimos minutos.');
