INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3352, 244, 14, 10, '2023-08-12', 180, 'Titular',null, '3', 'Buen partido. A pesar de ciertos errores en salida, fue bastante intenso, ganando bastates duelos, ayudando en labores defensivas constantemente. En el segundo tiempo tuvo muy buenas salidas asociandose de muy buena forma.');
