INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3355, 244, 14, 10, '2023-08-12', 163, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3356, 244, 14, 10, '2023-08-12', 182, 'Titular',null, '2', 'No fue uno de los mejores partidos de Esteban. Perdió muchos duelos defensivos y se le vió muy acelerado en querer recuperar el balón, provocando muchas faltas que el equipo rival busca aprovechar lanzando balones largos directos a nuestra área.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3357, 244, 14, 10, '2023-08-12', 181, 'No citado',null, null, null);
