INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3359, 244, 14, 10, '2023-08-12', 162, 'Titular',null, '3', 'Luchó durante todo el partido. A pesar de tener una marca personal durante todo el partido, buscó generar juego ofensivo constantemente descendiendo para aguantar balones o descargar de primera para los compañeros que venían de frente. Marcó un muy buen gol para el descuento del equipo. Ayudó bastante en labores defensivas durante todo el partido.');
