INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3497, 257, 16, 11, '2023-04-22', 189, 'Titular',null, '4', 'Todos nuestros inicios partían por él, fue pieza clave en nuestro bloque defensivo entregando seguridad, buenas coberturas y comunicación. Debe mejorar la interpretación de cuando romper con una conducción o un pase. Autor del primer gol nuestro proveniente de un rebote que da el arquero rival desde una táctica fija.');
