INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3504, 257, 16, 11, '2023-04-22', 194, 'Titular',null, '5', 'Entrego el equilibrio defensivo y ofensivo en todos los momentos del juego, colaborativo con todo el equipo, desde los fundamentos técnicos fue el mejor.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3505, 257, 16, 11, '2023-04-22', 202, 'Titular',null, '2', 'Le costo mucho entrar en juego, en algunas zonas de la cancha eludia y nos dejaba mano a mano, cuando empezo a aparecer un poco más en el juego cambio un poco, tuvo remates de media distancia pero no colaboro mucho en nuestra fase defensiva.');
