INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3507, 257, 16, 11, '2023-04-22', 186, 'Titular',null, '4', 'Gano la mayoria de los duelos ofensivos, se apoyo cuando fue necesario. Debe mejorar el donde interpretar cuando y en que zona de la cancha enfrentarse a duelo. Un gol nuestro salio de una asistencia de él y tuvo varias ocasiones de la misma manera que no fueron concretadas.');
