INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3528, 258, 16, 11, '2023-04-29', 202, 'Titular',null, '3', 'Dio lineas de pase claras cuando teniamos que asociarnos para circular el balón, debe interpretar mejor en que zonas de la cancha debe eludir y sus retornos deben ser más rápidos. Se asocio con compañeros generando peligro constante en portería rival.');
