INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3758, 268, 16, 11, '2023-08-03', 202, 'Titular',null, '4', 'Buen partido, se asocio bien, busco ir hacia adelante, buenos retornos cuando perdíamos el balón, debe mejorar su interpretación de en que zonas eludir.');
