INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3763, 268, 16, 11, '2023-08-03', 193, 'Suplente ingresa',null, '2', 'Ingreso de manera regular, intento ir hacia adelante, perdio y gano duelos, se quedaba mucho en ofensiva sin coolaborar en fase defensiva.');
