INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3778, 269, 16, 11, '2023-07-29', 187, 'Suplente ingresa',null, '3', 'Ingreso bien, se proyecto en ofensiva, buenas asociaciones y defensivamente en ocasiones perdio referencias de la portería pero no de su marca.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3779, 269, 16, 11, '2023-07-29', 200, 'Suplente ingresa',null, '3', 'Le costo un poco entrar en la dinámica del equipo, hasta que ajusto y pudo complementarse bien, proyectandose, eludiendo aunque a veces impreciso.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3780, 269, 16, 11, '2023-07-29', 194, 'Titular',null, '4', 'Buen partido, de un comienzo errático en los pases, ajusto y empezo a alimentar mediante buenas asosiaciones a los extremos y delantero centro. Interpreto y valoro de buena manera cuando rematar de media distancia con espacio.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3781, 269, 16, 11, '2023-07-29', 202, 'Titular',null, '2', 'Estuvo impreciso y no valoro de buena manera cuando asociarse en ofensiva, si estuvo bien en los duelos ofensivos y cuando debio ejercer presión post pérdida, buena actitud.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3782, 269, 16, 11, '2023-07-29', 191, 'Titular',null, '3', 'Buen partido, le costo adapartse al terreno de juego, pero se proyecto constantemente en ofensiva asociandose y eludiendo, muy buena la comunicación con sus compañeros.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3783, 269, 16, 11, '2023-07-29', 186, 'Titular',null, '4', 'Muy buen partido, fue peligro constante en campo rival, gano duelos por ambas bandas, generaba desequilibrio y remato a portería en ocasiones que genero bastante peligro.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (3784, 269, 16, 11, '2023-07-29', 190, 'Suplente ingresa',null, '2', 'Le costo entrar un poco en el juego, en ocasiones impreciso y no entregando bien los pases, también se deshacía muy rápido del balón y cuando ajusto se vio mucho mejor sobre todo en ofensiva.');
