INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5476, 217, 13, 9, '2023-09-24', 136, 'Suplente ingresa',null, '2', 'El partido estaba muy apretado cuando ingresó Sebastian y le costó entrar en juego sin embargo eso le permite entender como debe integrarse en esa dinamica, asi ocurrio porque cuando tuvo que ayudar a defender lo hizo y cuando recibio ayudo a darle continuidad a nuestro juego.');
