INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5478, 217, 13, 9, '2023-09-24', 154, 'Suplente ingresa',null, '2', 'Gaspar ingreso los últimos momentos del juego, no era fácil porque ese extremo nos dio mucho trabajo, pero Gaspar se acomodó rápido y logró posicionarse en el juego ayudando al equipo a tener el equilibrio en la linea defensiva.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5479, 217, 13, 9, '2023-09-24', 145, 'Suplente ingresa',null, '2', 'Eduardo es un volante que juega bastante bien pese a que aun no se desarrolla como el resto, su físico es mas menudo que el resto de esa zona, pero él no rehuye el juego friccionado, y muestra valentia en es afceta. Ayudo a que el equipo metuviera el control del juego y se generara alguna opciones de gol para haber aumentado el marcador.');
