INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5647, 185, 11, 8, '2023-10-03', 115, 'Titular', 3, '4', 'Muy buen desempeño. Se coordinó muy bien con sus compañeros de línea. Comunicativo, bien en los duelos, juego aéreo y coberturas defensivas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5648, 185, 11, 8, '2023-10-03', 116, 'Titular', 4, '4', 'Muy buen desempeño por su banda. Tuvo mucha proyección en ataque durante el juego. En fase defensiva estuvo seguro y generalmente ganó sus duelos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5649, 185, 11, 8, '2023-10-03', 117, 'Titular', 12, '5', 'Sobresaliente desempeño. Buena continuidad en el juego por la banda izquierda. Comenzó como extremo derecho, luego cambió a la banda izquierda. Pasó de jugar de extremo a lateral izquierdo. Muy bien en las fases del juego.');
