INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5655, 185, 11, 8, '2023-10-03', 123, 'Titular', 14, '3', 'Buen desempeño. Lo que intervino en el juego lo hizo bien. Debe mejorar su movilidad y el jugar perfilado ya que tiende a jugar para atrás.');
