INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5664, 218, 13, 9, '2023-10-03', 132, 'Titular', 14, '3', 'Justin hizo un buen partido, comenzó como volante por derecha y en el segundo tiempo paso como volante central. En ambas posiciones lo hizo bien y ayudo en la recuperacion en esa zona del campo.');
