INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (5667, 218, 13, 9, '2023-10-03', 137, 'Titular', 9, '3', 'Franco es un jugador destacado que ha ido mejorando con el correr del año, y está tomando protagonismo en el juego. En este partido tuvo espacios para generar opciones de gol por ese carril y habilitar a compañeros en posciones de zona de fialización. Buen partido.');
