INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6084, 251, 15, 10, '2023-10-18', 171, 'Titular', 14, '3', 'Buen partido. Se le vió muy activio y participativo en ambas fases del juego, recuperando muchos balones en campo rival y ganando sus duelos ofensivos constantemente provocando bastante daño al equipo rival.');
