INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6734, 225, 13, 9, null, 139, 'Titular', 3, '3', 'Cristian jugó un buen partido, muy seguro en su juego, seguro con el balon un par de jugadas en que se equivoco, sin embargo siempre empujo al equipo, salio jugando, gano en los duelos. Buen partido de Cristian.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6735, 225, 13, 9, null, 140, 'Titular', 13, '2', 'Cesar es nuestro goleador, en este partido hizo un gol sin embargo desde ya hace varias fecas viene mostrando un juego bajo en su rendimiento, se le nota erratico en la toma de decisiones y eso le ha impedido convertir en acciones que antes las convertía.');
