INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6737, 225, 13, 9, null, 142, 'Titular', 11, '4', 'Javier es un jugador que cada vez mejora en su juego, la semana anterior viajo con la sub 14 al torneo de Punta Arenas y jugó todos los partidos y hoy fue el jugador mas destacado en la cancha, su rendimiento va en alza.');
