INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6739, 225, 13, 9, null, 144, 'Titular', 4, '3', 'Samuel es un buen marcador, le cuesta ofensivamente sin embargo en la marca anda bien. Hay partidos como este donde se requiere que se desdoble para generar rompimientos por banda pero a él le cuesta, sin embargo es fuerte y para contener esta bien.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6740, 225, 13, 9, null, 145, 'Suplente ingresa', 8, '2', 'Eduardo es un jugador que pone muchas ganas en el juego, pero su cremiento va un tanto retrasado al del promedio y eso le pasa la cuenta, hoy puso ganas pero le cuesta en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6741, 225, 13, 9, null, 146, 'Titular', 5, '2', 'Adiel es un jugador que el año pasado jugaba como central, y en este año por su estatura lo ubique como volante central, el primer semestre lo hizo en la primera etapa del torneo de apertura, luego lo ubique como volante central y mostro un juego que le ayudo al equipo sin embargo su buena técnica se ve opacada por su lentitud y eso ahora no le ayuda al equipo, regular partido de Adiel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6742, 225, 13, 9, null, 147, 'Titular', 12, '2', 'Al igual que varios jugadores de la categoría Kevin tiene un crecimiento tardío, esta lejos del promedio, por se motivo le cuesta en los partidos. Hoy no pudo nunca desequilibrar por su banda, mas bien fue absorvido por su marcador y eso le impide al equipo tener equilibrio en ataque porque por el sector derecho con Donoso es todo lo contrario. Bajo partido de Kevin');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6743, 225, 13, 9, null, 148, 'Suplente ingresa', 11, '3', 'Isack es un jugador de mucha habilidad con el balon, hoy primero ingreso en el sector medio como inteior derecho y despues paso a jugar de extremo, en esta posición genero bastante peligro y le permitio convertir un gol. Buen partido los minutos que jugó como extremo de Isack.');
