INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6996, 226, 13, 9, null, 136, 'Titular', 8, '2', 'Sebastian jugó el primer tiempo, intentó hacer lo que hace siempre en los entrenamientos. Busco posicionarse en esa zona sin embargo le costó mucho debido principalmente a que su fisico no lo acompaña, elude porque tiene la habilidad y la tecnica pero pierde la ventaja porque el rival es mas fuerte y asi todo el primer tiempo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6997, 226, 13, 9, null, 137, 'Titular', 9, '3', 'Franco es un jugador muy interesante, con caracteristicas para ser un jugador de proyección sin embargo hay  omentos en qe les cuesta resolver adecuadamente y se termina enredadno. Asi ocurrio en los ultimos partidos, en el partido anterior ingreso desde el banco y le sirvio porque mejoró y en este partido ta,bién lo hizo bien. Franco debe continuar mejorando.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6998, 226, 13, 9, null, 138, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (6999, 226, 13, 9, null, 139, 'Titular', 3, '2', 'Cristian es otro jugador que ha progresado mucho, es un defensa con biotipo interesante e izquierdo. Su crecimiento ha sido en lo tactico, y tecnicamente tambien a progresado mucho. Aun le cuestan algunas cosas porque a pesar de ser grande sigue siendo un niño. Buen jugador pero hoy cometio algunas equivocaciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7000, 226, 13, 9, null, 140, 'Titular', 13, '2', 'Cesar es otro jugador muy interesante por su habilidad con el balon, además es un jugador que es engañoso en lo fisico porque no es tan grande pero si es muy resistente. Sin embargo el ultimo tercio del torneo a venido disminuyendo su rendimiento, y porque esta jugando como delantero centro porque Renato Ortiz lleva tiempo lesionado y por ese motivo no a tenido pausas, juega siempre de titular. Hoy estuvo lejos de su nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7001, 226, 13, 9, null, 141, 'Lesionado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7002, 226, 13, 9, null, 142, 'Titular', 11, '3', 'Javier hoy tuvo pocas opciones de desnivelar porque le toco un marcador rival muy rapido y fuerte sin embargo se presento ahi un lindo duelo. Javier es capaz de adaptarse rápido a esos momentos y es un jugador polivalente, puede jugar como volante y como delantero eso le ayuda a resolver bien casi todas las situaciones. Hoy le costo un poco mas pero aun asi fue uno de los pocos de mitad hacia arriba que mantuvo el nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7003, 226, 13, 9, null, 143, 'Suplente ingresa', 9, '2', 'Mateo ingreso en los ultimos minutos, y jugó con ganas pero al igual que varios de la categoría tiene habilidad con el balon, intenta desarrollar el juego poniendo en practica esas virtudes pero su poco desarrollo físico le impide superar esos momentos sobre todo de duelo. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7004, 226, 13, 9, null, 144, 'Titular', 4, '2', 'Samuel es un jugador que para su edad es fuerte y logra posicionarse bien en la funcion de lateral, en los duelos por lo general gana, defensivamente bien, sin embargo le cuesta cuando esta en fase ofensiva en zona de fibalizacion, ahi le cuesta resolver.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7005, 226, 13, 9, null, 145, 'Suplente ingresa', 8, '2', 'Eduardo es otro de los jugadores que tiene condiciones pero por ahora tiene un desarrollo tardio, le cuesta imponerse en el juego porque los rivales son mas grandes.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7006, 226, 13, 9, null, 146, 'Titular', 5, '3', 'Adiel comenzo este año como defensa central sin embargo por su estatura lo ubique como un volante central, además como centrales hay tres jugadores de mejor bitipo que él. Adiel es un jugador tecnicamente bien dotado y cuando lo empece a  hacer jugar como volante central se vio muy bien y el equipo mejoro en su juego sin embargo la ultima parte del torneo le ha costado principalmente porque es un tanto lento para resolver, esto creo se deba a que el resto de jugadores esta creciendo y el se ha ido quedando o debemos esperar a que el tambien crezca. Hoy jugó un regular partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7007, 226, 13, 9, null, 147, 'Suplente ingresa', 13, '2', 'Kevin es otro de los casos de esta categoria, es un jugador interesante una apuesta a futuro, ya que por su desarrollo fisico retrasado respecto de la media le cuesta en partidos donde se impone lo fisico. Hoy ingreso en los ultimos minutos y le costo entrar en juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7008, 226, 13, 9, null, 148, 'Suplente ingresa', 11, '2', 'Isack es un jugador que tiene habilidad individual, es bueno en el uno contra uno, por lo general pasa, pero es otro jugador que aun está muy retrasado en lo fisico respecto de la media, pero aun asi tiene cualidades que lo desde mi vision lo hacen destacar. Hoy ingreso como delantero y los minutos que jugó se vio muy inquieto y buscando desnivelar. Pero en general le costo como al resto de nuestros delanteros.');
