INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7219, 228, 13, 9, null, 146, 'Titular', 5, '2', 'Adiel es un jugador que mejoró en su juego, pasando de defensa central a jugar como volante central. Este le permitio al equipo mejorar el juego y competir de mejor forma con los rivales, sin embargo en esta parte del año también se le ha visto un bajon sobre todo fisico, Adiel es un jugador que no es muy rápido, y por ese motivo le ha costado en esta parte del año.');
