INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7227, 228, 13, 9, null, 154, 'Suplente ingresa', 4, '2', 'Gaspar es un jugador que tiene cualidades como defensor, pero aun no se define en que puesto porque aun esta muy chico de edad a pesar que tiene un buen biotipo. Pienso que se está perfilando en esa posicion pero debiera continuar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7228, 228, 13, 9, null, 155, 'Titular', 9, '2', 'Claudio es un jugador interesante, de mucho talento pero que al igual que varios compeñeros tuvo un bajon en lo futbolístico, creo debido a su desarrollo. Aun no se define bien su función y puesto pero por sus cualidades es un buen volante o puede ser un buen extremo sin embargo este bajon puede influir en su desarrollo, debemos esperar para definir su puesto.');
