INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7230, 228, 13, 9, null, 157, 'Titular', 2, '2', 'Martin es un jugador que tiene mucha fortaleza fisica sin embargo le cuesta mucho con el balon, y eso le afecta en el juego porque va perdiendo confianza. En este partido fue de mas a menos, los primeros minutos se vio bien sin embargo ha medida que el rival fue mejorando y los extremos empezaron a tener mayor protagonismo se le vio complicado para resolver.');
