INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7462, 569, 16, 16, '2024-03-07', 47, 'Titular', 5, '2', 'Partido regular en el juego, muy errático en los pases y entregas de balón, perdió agresividad en fase defensiva, no interpreto los espacios de buena manera. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7463, 569, 16, 16, '2024-03-07', 50, 'Titular', 3, '4', 'Buen partido, mantiene un gran nivel en fase defensiva, entrega mucha seguridad al equipo, fuerte en duelos y rápido en coberturas, debe mejorar la entrega de balón y tomar mejores decisiones en juego ofensivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7464, 569, 16, 16, '2024-03-07', 51, 'Titular', 11, '4', 'Buen partido, tiene buenos desmarques, ganar muchos duelos ofensivos y tuvo un gran compromiso en fase defensiva, termino sustituido por ser su primer partido luego de una lesión.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7465, 569, 16, 16, '2024-03-07', 52, 'Titular', 1, '2', 'Partido bajo, es un jugador que entrega mucha seguridad en la porteria, pero estuvo muy erratico en los dos goles recibidos, tomo malas decisiones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7466, 569, 16, 16, '2024-03-07', 53, 'Titular', 10, '4', 'No fue un partido perfecto, simplemente porque debe mejorar en el control de su temperamento, eso le impide competir al 100% en algunos pasajes del juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7467, 569, 16, 16, '2024-03-07', 56, 'Suplente ingresa', 14, '3', 'Ingresó en el minuto 75, para entregar un mejor juego en la progresión y cumplió de buena manera, también mencionar que mejoro mucho en fase defensiva, teniendo una gran entrega en el juego, debe seguir en la mejora de intensidad ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7468, 569, 16, 16, '2024-03-07', 76, 'Suplente ingresa', 13, '3', 'Ingreso en el minuto 65, logro entregarnos un referente de área y competir con centrales rivales, cumplió y logro convertir el gol del empate, debe seguir en la mejora de la intensidad ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7469, 569, 16, 16, '2024-03-07', 83, 'Titular', 5, '3', 'Bueno partido desde la intensidad y agresividad en el juego y fase defensiva, debe mejorar sus controles y pasada de balón, tener mejor lectura en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7470, 569, 16, 16, '2024-03-07', 67, 'Titular', 12, '4', 'Buen partido, cumplió por banda, gano profundidad y duelos ofensivos, se asoció en zona de progresión y finalización, debe seguir en la mejora de entregar más solides a su juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7471, 569, 16, 16, '2024-03-07', 68, 'Titular', 4, '3', 'Buen partido desde la intensidad y agresividad, pero debe seguir en la mejora de sus controles y entender el juego en fase ofensiva, su velocidad le ayuda en los momentos que pierde su posición y debe tener regresos rápidos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7472, 569, 16, 16, '2024-03-07', 286, 'Suplente ingresa', 3, '3', 'Ingreso en el minuto 75, para formar línea de 3 defensores y buscar un mayor volumen en ataque, gano duelos pero sigue errando en los momentos de retardar o presionar, al no ser un jugador tan rápido, tiene complicaciones en los regresos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7473, 569, 16, 16, '2024-03-07', 72, 'Suplente ingresa', 10, '4', 'Ingreso en el minuto 60, para tener mayor control en el juego y mejor progresiones, cumplió en fase ofensiva y sobre todo tuvo un gran compromiso en la fase defensiva,');
