INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7553, 529, 16, 15, '2024-03-12', 393, 'Suplente ingresa', 8, '4.8', 'Jugadora que estuvo en toda la cancha, intervino de manera positiva, marco un gol y le entrego buena dinbamica al equipo.');
