INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7559, 529, 16, 15, '2024-03-12', 401, 'Titular', 11, '3.5', 'Jugadora tacticamente muy disciplinada, rapida e inteligente, debe ser mas gravitante en el juego.');
