INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7675, 498, 16, 2, '2024-03-18', 301, 'Suplente ingresa', 3, '2.5', 'Pocas acciones donde tuvo participación, correcto en ambas fases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7676, 498, 16, 2, '2024-03-18', 302, 'Suplente ingresa', 4, null, 'Jugó muy poco para ser evaluado');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7677, 498, 16, 2, '2024-03-18', 303, 'Titular', 3, '2.8', 'Con balón jugó muy bien, rotando por banda derecha en el pasillo interior. Generó mucho peligro sumandose al ataque. Sin balón, no estuvo seguro en los duelos ni tampoco en la marca dentro del área.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7678, 498, 16, 2, '2024-03-18', 307, 'Titular', 12, '2.3', 'Perdió muchos balones, malas decisiones, remates desviados, muchos fuera de juego. Solo le sube la nota que metió la asistencia para el gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7679, 498, 16, 2, '2024-03-18', 312, 'Suplente ingresa', 13, '2.5', 'Le tocó resolver situaciones de contra ataque. Las que tuvo para aguntar resolvió bien pero después no llegaba al área, tuvo un centro que paso por delante que tenía que ser de él');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7680, 498, 16, 2, '2024-03-18', 316, 'Suplente ingresa', 14, null, 'Jugó muy poco para ser evaluado');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7681, 498, 16, 2, '2024-03-18', 317, 'Titular', 3, '3.8', 'Uno de los mejores. Siempre atento a salir a anticipar, presionar lejos de su zona. Fuerte en los duelos, claro con el balón.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7682, 498, 16, 2, '2024-03-18', 320, 'Titular', 14, '2.5', 'Rendimiento irregular, intercambia entre pases buenos y errados. Defensivamente fuerte, intenso, podría haberse ganado amarilla. Cometió muchas faltas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7683, 498, 16, 2, '2024-03-18', 321, 'Titular', 13, '2.5', 'Partido ingrato, no tuvo chances de gol. Los balones que le jugaron en zona media siempre descargó correctamente.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7684, 498, 16, 2, '2024-03-18', 611, 'Titular', 11, '4', 'El más peligroso del ataque de la U. Bien llegado a rematar los centros. En una acción individual tuvo el 2-0. Bien en defensa, siempre marcando a un volante.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7685, 498, 16, 2, '2024-03-18', 612, 'Titular', 5, '3', 'Muy bien con balón, te da un tiempo más cuando se la juegan. En defensa estuvo correcto pero de le escapó su marca un par de veces que llegó a rematar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7686, 498, 16, 2, '2024-03-18', 613, 'Titular', 2, '3', 'Bien con balón, se asociació muy bien por el lado derecho. Llegó un par de veces al fondo pero sacó malos centros. Bien en defensa.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7687, 498, 16, 2, '2024-03-18', 614, 'Titular', 3, '2.8', 'Estuvo bien en los duelos defensivos pero mal en las transiciones. Se paró dos veces corriendo hacia atrás y permitió que nos filtraran pase. Ha mejorado con balón.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7688, 498, 16, 2, '2024-03-18', 615, 'Titular', 1, '3.3', 'Muy bien defensivamente. Le llegaron mucho más este partido y se mostró muy seguro en sus acciones. Luego con balón no nos presionaron así que no tuvo mucha participación.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7689, 571, 16, 16, '2024-03-18', 627, 'Titular', 13, '4', 'Muy Buen Partido, tuvo muchas intervenciones en el juego, sostuvo y entrego buenas descargas a jugadores de frente, volvió a convertir un gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7690, 571, 16, 16, '2024-03-18', 33, 'Titular', 3, '2', 'Desempeño Regular, tiene grandes problemas con su velocidad, tuvo muchas complicaciones en los duelos 1 v/s 1, retrocede y entrega terreno para ser encarado donde no es una de sus virtudes, ocupa muy poco su biotipo. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7691, 571, 16, 16, '2024-03-18', 46, 'Suplente ingresa', null, '3', 'Buen Desempeño, entro para darnos un nuevo aire en banda y tuvo muchas participaciones en ambas fases, aun se nota su falta de ritmo en competencia, pero entrego oficio en muchas acciones de juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7692, 571, 16, 16, '2024-03-18', 47, 'Titular', 5, '4', 'Muy Buen Partido, mucha intensidad en fase defensiva, cometió muy pocos errores en los inicios y progresión del juego, fue nuestro equilibrio en ambas fases, subió mucho su rendimiento desde el juego simple.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7693, 571, 16, 16, '2024-03-18', 50, 'Titular', 3, '1', 'La nota esta relacionada con los hechos cometidos dentro de un altercado con el rival, lo que le significo una roja directa, siendo el capitán del equipo debe ser el ejemplo en comportamiento deportivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7694, 571, 16, 16, '2024-03-18', 51, 'Titular', 6, '4', 'Muy Buen Partido, tuvo un gran recorrido por banda, estuvo en ambas fases de juego, cuando busco balones al espacio, es un jugador muy desequilibrante, debe mejorar en solidificar mas su juego por mas minutos de competencia.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7695, 571, 16, 16, '2024-03-18', 52, 'Titular', 1, '1', 'La nota esta relacionada con la reacción que tuvo frente  a un rival, posterior a un lanzamiento penal, este hecho termino con un gran altercado y con su expulsión por agresión al rival.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7696, 571, 16, 16, '2024-03-18', 53, 'Titular', 10, '1', 'La nota esta relacionada con su expulsión, no teniendo certeza de su participación en actos de indisciplina, fue de igual manera expulsado e informado de agresión a un rival.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7697, 571, 16, 16, '2024-03-18', 54, 'Titular', 13, '3', 'Buen Desempeño, pero siempre se espera mas de Héctor, es un jugador con mucho potencial, estuvo muy errático en sus controles y poca asociaciones en fase ofensiva, las veces que logros estar certero en el juego, demuestras su grandes condiciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7698, 571, 16, 16, '2024-03-18', 57, 'Titular', 3, '4', 'Muy Buen Partido, ha tenido un gran rendimiento en una posición de defensor central, fuerte en los duelos defensivos, entrego buenos inicios y sostuvo de gran manera las contras del rival, debe seguir mejorando en posicionamiento defensivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7699, 571, 16, 16, '2024-03-18', 76, 'Suplente ingresa', 13, '3', 'Buen Desempeño, entro para tener un nuevo aire en ataque, que nos ayudara a seguir jugando en campo rival, compitió de buena manera, se asocio y genero ocasiones de gol para nuestro equipo, debe seguir mejorando en tener un juego mas contundente.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7700, 571, 16, 16, '2024-03-18', 83, 'Suplente ingresa', 14, '2', 'Desempeño Regular, entro para entregarnos mayor dinámica en zona media y un mejor juego, con sus características, pero entro muy ansioso y con muchos errores en acciones individuales.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7701, 571, 16, 16, '2024-03-18', 67, 'Titular', null, '4', 'Muy Buen Partido, en los primero minutos estuvo erratico en sus acciones pero con el correr de los minutos fue teniendo un  juego mas contundente en banda, tuvo un gran recorrido y participo en el primer gol con  una gran habilitación de centro, debe seguir confiando y creciendo en base a sus capacidades.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7702, 571, 16, 16, '2024-03-18', 72, 'Suplente ingresa', 10, '5', 'Gran Desempeño, su juego ha ido en evolución con el correr de los partidos y minutos en cancha, es un jugador con muchas capacidades en el juego individual, resuelve de buena manera y nos entrego un mejor juego con su ingreso, tuvo mucha movilidad y convirtió nuestro segundo gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7703, 571, 16, 16, '2024-03-18', 77, 'Titular', 5, '5', 'Gran Desempeño, Elías cada partido logra tener un mejor crecimiento en el juego, tiene gran capacidad física y futbolística, resuelve de gran manera al tener grandes controles de balón, puede competir en distintas posiciones y siempre a un gran nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7704, 571, 16, 16, '2024-03-18', 61, 'No citado',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7705, 571, 16, 16, '2024-03-18', 656, 'Suplente ingresa', 3, '3', 'Buen Desempeño, fue su debut con el equipo, nos entrego velocidad y seguridad en zona defensiva, entrego buenos inicios, pero su ímpetu a mostrar sus aptitudes le hicieron cometer errores, pero debe seguir en la mejora y seguramente nos ayudara mucho en zona defensiva.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7706, 601, 16, 17, '2024-03-19', 126, 'Titular', 13, '4', 'Jugador que llevo a cabo muy bien las intrucciones dado a traves del plan de partido...anotando 2 goles en la victoria. Buen rendimiento!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7707, 601, 16, 17, '2024-03-19', 119, 'Titular', 14, '4.2', 'Jugador que ha ido en ascenso en su rendimiento sobre todo en lo ofensivo...cumplio un rol importante en el engranaje del equipo. Bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7708, 601, 16, 17, '2024-03-19', 124, 'Titular', 4, '4', 'Primera que oficialmente juega en la posicion de lateral, donde su rendimiento estuvo a la altura del encuentro. Se puede decir en el aspecto defensivo tuvo un rendiento por sobre el promedio y en el ataque donde es fuerte, estuvo a la altura.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7709, 601, 16, 17, '2024-03-19', 122, 'Titular', 12, '4', 'Jugo en una nueva posicion y lo hizo de forma muy aceptable. Se movio con mucha dinamica en espacios libres y conecto muy bien con sus companeros mas cercanos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7710, 601, 16, 17, '2024-03-19', 108, 'Titular', 1, '4', 'Jugo bien aunque no tuvo mayor inconveniente ya que el equipo funciono de gran forma en el aspecto defensivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7711, 601, 16, 17, '2024-03-19', 113, 'Titular', 3, '4', 'Subio su nivel considerablemte en el aspecto tecnico, donde todavia tiene mucho margen de mejora. En el aspecto defensivo dindio a gran nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7712, 601, 16, 17, '2024-03-19', 116, 'Titular', 3, '4.4', 'Jugador con un gran nivel en este momento, que maneja el aspecto ofensivo y defensivo de forma optima. Muy bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7713, 601, 16, 17, '2024-03-19', 128, 'Suplente ingresa', 13, '3.5', 'Jugador que tiene un margen amplio de mejora...entro con muchas ganas pero su nivel fisico hoy todavia no le permite adaptarse bien a la dinamica alta de juego del equipo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7714, 601, 16, 17, '2024-03-19', 118, 'Titular', 5, '4', 'Tiene una gran tecnica y vision de juego...jugo un partido por sobre la media con mucha movilidad y dinamica. Le da al equipo el equilibrio necesario para dominar los momentos del juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7715, 601, 16, 17, '2024-03-19', 131, 'Titular', 13, '4.4', 'Tiene un talento de mucha proyeccion, siendo uno de los jugadores mas alto rendimiento en el equipo, se me=ueve de forma inteligente y desmarca a los espacios vacios con mucho ingenio. Es el goleador del equipo en estos momentos. Muy bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7716, 601, 16, 17, '2024-03-19', 111, 'Titular', 2, '4', 'En el aspecto fisico tiene una pretuberancia excelente, pero todavia en lo tecnico tiene un margen muy amplio de mejora. Jugo un buen partido en general pero puede ser mucho mejor.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7717, 601, 16, 17, '2024-03-19', 130, 'Titular', 11, '4', 'Tiene una dinamica muy importante, donde tiene un sacrificio individual, mezclado con lo fisico y tecnico lo hacen un jugador de proyeccion. Jugo un partido mas variado desde lo tactico, recibiendo en varias ocaciones entrelineas y asi abrir carriles para otro companero. Necesita mejorar su disparo desde media distancia (transversal).');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7718, 601, 16, 17, '2024-03-19', 123, 'Suplente ingresa', 14, '3.5', 'Ha tenido una baja considerable en su rendimiento en las ultimas 2 semanas...aunque en este partido no jugo del todo bien, lucho siempre y tambien hizo un partido muy respnsable. Mucho margen de mejora!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7719, 601, 16, 17, '2024-03-19', 117, 'Suplente ingresa', 4, '3.5', 'Jugo un partido muy responsable, tomando en cuenta que no ha jugado muchos minutos en la presente temporada. Entro con muchas ganas y tuvo una buena labor aunque tiene un margen muy amplio de mejora. Entrena muy bien y eso lo hace un jugador que siempre esta para ser considerado.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7721, 530, 16, 1, '2024-03-19', 386, 'Titular', 1, '4', 'Tuvo poca pero buenas intervenciones. Constante comunicación con su linea defensiva y para motivar al equipo a ir por más');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7722, 530, 16, 1, '2024-03-19', 388, 'Titular', 2, '4.5', 'Buen despliegue, bien defensivamente y siendo constante agente ofensivo. Faltó un poco de precisión al momento de finalizar las jugadas pero a grandes rasgos, gran partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7723, 530, 16, 1, '2024-03-19', 389, 'Titular', 4, '3.5', 'Bien defensivamente ganando muchos duelos. A veces se encontraba fuera de posición, sobre todo para hacer presión tras pérdida, quedando muy lejos de las delanteras rivales. Ofensivamente, bien, llegando a poblar el área constántemente.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7724, 530, 16, 1, '2024-03-19', 390, 'Suplente ingresa', 12, '3.5', 'Entró con buena actitud y disposición a participar del juego. Encontró espacios interiores con facilidad, pero le faltó precisión para finalizar las jugadas, ya sea asistiendo o rematando al arco.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7725, 530, 16, 1, '2024-03-19', 391, 'Suplente ingresa', 8, '3.5', 'Bien entregando equilibrio en el medio y rápida circulación cuando el rival defendía en bloque bajo. Defensivamente, faltó un poco de agresividad para ganar segundos balones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7726, 530, 16, 1, '2024-03-19', 393, 'Titular', 9, '4', 'Primer tiempo le costó dar velocidad a los pases para desarmar al rival, el segundo tiempo, pudo encontrar a compañeras en mejor posición para progresar. Bien en los segundos balones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7727, 530, 16, 1, '2024-03-19', 394, 'Titular', 3, '3', 'Buen trabajo de comunicación, tuvo pocos duelos defensivos. Terminó jugando muy fuera de posición, desordenando al equipo');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7728, 530, 16, 1, '2024-03-19', 395, 'Titular', 12, '3', 'Jugando a perfil cambiado le costó encontrar espacios para juego interior y por banda, no pudo generar tanto desequilibro ante una defensa bien cerrada.');
