INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7782, 557, 16, 5, '2024-03-25', 39, 'Suplente ingresa', 14, '2', 'ingreso sin personalidad no toco casi ningún balón desconectado del juego defensivo y ofensivo mal partido de los pocos minutos que jugo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7783, 572, 16, 16, '2024-03-25', 627, 'Titular', 13, '5', 'Su desempeño fue sobresaliente, los primero minutps estuvo errático, pero tuvo un gran alza en su juego con el correr del juego, sostuvo de muy buena manera y sus descargas siempre nos entregaron seguir con control del juego, mucha movilidad y logro convertir 2 goles.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7784, 572, 16, 16, '2024-03-25', 33, 'Suplente ingresa', 3, '3', 'Entro pocos minutos, pero logro cumplir con las instrucciones entregada, entro para darnos solides defensiva y cumplió de buena manera las tareas asignadas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7785, 572, 16, 16, '2024-03-25', 46, 'Titular', 3, '4', 'Muy buen partido, compitió en una posición con aspectos mas defensivos, pero como principal función  ser fuerte en los duelos y cerrar su zona, que nos entregara un mejor inicio, logro cumplir con las tareas asignadas, pero en ocasiones se sumaba con poco claridad en ataque, dejando un desequilibrio defensivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7786, 572, 16, 16, '2024-03-25', 47, 'Titular', 5, '4', 'Muy buen partido, gran intensidad en el juego, en ambas fases, es un jugador con grandes recorridos, siempre esta en disputas de duelos defensivos, debe ser mas agresivo en los duelos y seguir en la mejora de sus controles y pases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7787, 572, 16, 16, '2024-03-25', 51, 'Titular', 6, '4', 'Muy buen partido, es un jugador con gran potencial en los duelos, gano por banda y llego a  línea de fondo las veces que se atrevió, tuvo un gran recorrido por banda, en ambas fases tuvo una habilitación. Debe seguir en solidificar su juego y no pasar por pasajes que esta poco intenso y desconectado de las tareas a cumplir.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7788, 572, 16, 16, '2024-03-25', 54, 'Titular', 13, '3', 'Buen partido Héctor tiene un gran potencial, pero estuvo muy errático en el inicio del juego, fue amonestado y eso le complico para ir mejorando su juego, tuvo un mejor segundo tiempo y las veces que logro colocarse de frente, resolvió bien las acciones, cuando encontró espacios y los supo utilizar fue muy importante en la progresión y finalización del equipo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7789, 572, 16, 16, '2024-03-25', 55, 'Titular', 1, '4', 'Muy buen partido, tuvo grandes intervenciones y nos entregó solides en el arco, entrego tranquilidad en los inicios y tomo buenas decisiones en la mayoría de las acciones que participo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7790, 572, 16, 16, '2024-03-25', 57, 'Titular', 3, '5', 'Tuvo un gran desempeño, nos entregó mucha solidez defensiva, gano sus duelos y cerro su zona, siempre estuvo bien ubicado y realizo las coberturas de muy buena manera, a pesar de su velocidad, jamás fue sobrepasado y con su característica futbolística nos entrega muy buenos inicios.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7791, 572, 16, 16, '2024-03-25', 76, 'Suplente ingresa', 13, '3', 'Entro poco minutos, pero cumplió con las tareas asignadas, sostuvo y entrego buenas descargas, fue agresivo en la presión alta y compitió de buena manera  los duelos disputados.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7792, 572, 16, 16, '2024-03-25', 83, 'Suplente ingresa', 14, '3', 'Buen partido, entrego dinámica y un segundo aíre en zona media, nos dio  mas control desde los recorrido que ejecuto, debe seguir en la mejora de tomar mejores decisiones en el juego, mejorar sus controles y pases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7793, 572, 16, 16, '2024-03-25', 67, 'Titular', null, '5', 'Tuvo un desempeño sobresaliente, por sus grandes recorrido en banda, siempre muy importante en ambas fases, siempre estuvo bien ubicado en fase defensiva y entego mucha amplitud en fase ofensiva, gano la mayoría de sus duelos en disputa, tuvo un mejor juego asociado, logro convertir un gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7794, 572, 16, 16, '2024-03-25', 68, 'Suplente ingresa', 6, '3', 'Buen partido, entro en un perfil que no domina, pero desempeño de buena manera las tareas asignada, cerro su banda y se sumo en fase ofensiva de buena manera, nos entregó velocidad en una zona que teníamos deficit, en los últimos minutos jugo por su perfil y también tuvo un buen desempeño.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7795, 572, 16, 16, '2024-03-25', 72, 'Titular', 10, '5', 'Gran desempeño en su partido, Bastián tiene una gran lectura de juego, mucha habilidad en el 1V/S1, tuvo un partido donde nos entregó sus condiciones y fue un jugador muy importante en fase ofensiva, siempre ocupo de buena manera los espacios libres, tuvo movilidad y grandes habilitaciones en zona de finalización, se asoció constantemente, fue simple en el juego, convirtió un gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7796, 572, 16, 16, '2024-03-25', 77, 'Titular', 14, '4', 'Muy buen partido, Elías entrega mucha dinámica al juego, entrego mucho equilibrio y ocupo de buena manera los espacios libres, entendió cuando ser segunda línea de ataque y cuando sostener para ser apoyo en la rotación del juego, tuvo que abandonar el juego por problemas estomacales eso lo fue desgastando en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7797, 572, 16, 16, '2024-03-25', 61, 'Suplente ingresa', 14, '3', 'Entro pocos minutos, pero cumplió con las tareas asignadas, se posiciono en un segunda línea de 4 y entro en juego con su buena técnica individual, cerro su zona en fase defensiva y ayudo en cerrar el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7798, 572, 16, 16, '2024-03-25', 656, 'Titular', 3, '4', 'Muy buen partido, entrego solidez defensiva, gano la mayoría de los duelos que disputo, agresivo en la marca y muy rápido en las coberturas muy buen juego aéreo, debe tener mayor tranquilidad en fase ofensiva, tomar mejores decisiones en los inicios.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7799, 531, 16, 15, '2024-03-26', 388, 'Titular', 2, '4.5', 'Constante agente ofensiva, entrego solidez y supo jugar de buena manera en el que no era su perfil');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7800, 531, 16, 15, '2024-03-26', 389, 'Titular', 4, '4', 'Jugadora que cumple con las necesidades del equipo, es sustituida para buscar alternativas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7801, 531, 16, 15, '2024-03-26', 390, 'Titular', 12, '4', 'Desempeño correcto, entrego descanso al equipo, pudo ser mas significativa en  el resultado.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7802, 531, 16, 15, '2024-03-26', 391, 'Suplente ingresa', 2, '4.8', 'Gran desempeño, entrego intensidad y soluciones en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7803, 531, 16, 15, '2024-03-26', 393, 'Titular', 5, '4', 'Jugadora de gran despliegue, no se vio tan fina como en otras ocasiones sin embargo tuvo un buen rendimiento.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7804, 531, 16, 15, '2024-03-26', 394, 'Titular', 3, '4.6', 'Buena ubicacion, solida en duelos, jerarquia al momento de jugar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7805, 531, 16, 15, '2024-03-26', 397, 'Suplente ingresa', 5, '3.5', 'Entro en un momento dificultoso a resolver un problema de pases errados, no alcanzo a encontrar el rendimiento esperado.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7806, 531, 16, 15, '2024-03-26', 400, 'Titular', 9, '3.8', 'Jugadora que esperabamos un poco mas, buen despliegue, el cambio de posicion no le favorecio en funcion a sus condiciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7807, 531, 16, 15, '2024-03-26', 401, 'Suplente ingresa', 11, '4.6', 'Gran desempeño, si bien erro un par de ocasiones de gol, entrego salida clara, intensidad y profundidad al equipo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7808, 531, 16, 15, '2024-03-26', 403, 'Titular', 13, '4.8', 'Gran desempeño, esfuerzo y sacrificio, entrego descanso, lucho balones y se creo opciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7809, 531, 16, 15, '2024-03-26', 410, 'Suplente ingresa', 13, '4', 'dificil de evaluar ya que jugo muy pocos minutos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7810, 531, 16, 15, '2024-03-26', 494, 'Titular', 3, '4', 'Jugo un partido solido, lamentablemente tuvo un error de concentracion que significo el partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7811, 531, 16, 15, '2024-03-26', 507, 'Titular', 5, '3.5', 'gran despliegue, se noto su jerarquia, sin embargo perdio muchos balones durante el jugo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7812, 531, 16, 15, '2024-03-26', 620, 'Titular', 1, '3.5', 'Se noto nerviosa, con errores que no acotumbramos a ver en ella, jugo lejos del arco lo que hizo que el rival no tuviera profundidad.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7813, 531, 16, 15, '2024-03-26', 622, 'Titular', 11, '4', 'Jugadora que cumple con las necesidades del equipo, es sustituida para buscar alternativas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7813, 494, 16, 2, '2024-03-28', 633, null,null, '2.5', 'Veníamos de un partido discreto contra Ohiggins y no hubieron cambios. Partido muy similar en plantemiento y no jugamos bien. No realizó los cambios que correspondían según rendimiento individual. Tardó en sacar a un defensa por un jugador más ofensivo para ir a buscar el trinunfo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7814, 494, 16, 2, '2024-03-28', 280, 'Titular', 4, '2.5', 'Bastante bien en ataque, tiene jugadas de mucho peligro entre centros y remates. Defensivamente está muy lejos del lateral rival que tiene que marcar. Físicamente no le da para jugar al ritmo que se necesita y siempre termina pidiendo cambio.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7815, 494, 16, 2, '2024-03-28', 301, 'Suplente ingresa', 3, '2.8', 'Bien con el balón, no perdió pases fáciles. Defensivamente tuvo una transición defensiva a su espalda que resuelve muy bien pero tuvo otro mano a mano con García que se lo lleva muy fácil en velocidad por la banda.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7816, 494, 16, 2, '2024-03-28', 302, 'Suplente ingresa', 4, null, 'Jugó muy poco para ser evaluado.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7817, 494, 16, 2, '2024-03-28', 303, 'Titular', 3, '1.5', 'El peor del partido. Muy errático con balón, perdió muchos pases fáciles. Defensivamente muy deficiente, de un error de él llega el empate.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7818, 494, 16, 2, '2024-03-28', 307, 'Titular', 12, '2', 'Uno de sus partidos más bajos. En el juego asociado fue muy poco aporte y tampoco fue peligroso de cara a portería. Defensivamente casi no aportó.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7819, 494, 16, 2, '2024-03-28', 312, 'Suplente ingresa', 13, '2', 'Entró y no logró darle otra cara al ataque. Tuvo un par de acciones que podría haber teminado mejor al arco. Defensivamente tampoco entró a presionar a mil.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7820, 494, 16, 2, '2024-03-28', 316, 'Suplente ingresa', 14, '2.5', 'Bien los minutos que le tocó disputar. Con personalidad pidió el balón y jugó para adelante. Correcto en defensa, metió bastante.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7821, 494, 16, 2, '2024-03-28', 317, 'Titular', 3, '3.8', 'De los mejores en el partido. Como siempre muy sólido en defensa, ganó muchos de sus duelos defensivos. Casi se gana una expulsión tontamente.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7822, 494, 16, 2, '2024-03-28', 320, 'Titular', 14, '2.8', 'Correcto partido, regular en su rendimiento. Corrió bastante, estuvo siempre atento a la presión post pérdida. Se atrevió muy poco a jugar hacua delante.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7823, 494, 16, 2, '2024-03-28', 321, 'Titular', 13, '3', 'Tuvo pocas acciones dentro del área, pero el remate que tuvo fue gol. Después en el juego pudo aportar poco, no estuvo preciso en sus descargas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7824, 494, 16, 2, '2024-03-28', 611, 'Titular', 11, '2.5', 'Mal partido para lo que venía mostrando. No pudo demostrar su habilidad en los duelos ofensivos contra un lateral izquierdo improvisado. Muy comprometido en defensa.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7825, 494, 16, 2, '2024-03-28', 612, 'Titular', 5, '3', 'No fue uno de sus mejores partidos pero sin duda es el mejor en el medio. Aporta mucha calidad al juego construido. Erró unos cuantos pases pero por intentar jugar de primera hacia delante. Defensivamente no se vio tan bien fisicamente, debía cubrir mucho espacio.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7826, 494, 16, 2, '2024-03-28', 613, 'Titular', 2, '4', 'El mejor del partido.El más peligroso en ataque, pudo encarar hacia dentro muchas veces y terminar en remate. Defensivamente no tuvo mucho trabajo por su lado.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7827, 494, 16, 2, '2024-03-28', 614, 'Titular', 3, '3', 'Muy buen partido hasta que fue sustituido en el entre tiempo por estar amonestado. Clave en defensa, con un par de cruces importantes dentro del área.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7828, 494, 16, 2, '2024-03-28', 615, 'Titular', 1, '2.5', 'No tuvo mucho que hacer en los goles, fueron muy buenas definiciones de Munder. Con balón nos presionaron poco y tampoco pudo ser determinante en las salidas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7829, 494, 16, 2, '2024-03-28', 617, 'Suplente ingresa', 13, null, 'Jugó muy poco para ser evaluado.');
