INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7995, 500, 16, 2, '2024-04-08', 307, 'Titular', 12, '3', 'Partido que mejoró mucho en el aspecto colectivo, se asoció mucho más, fue generoso. Defensivamente se desliga demasiado del juuego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (7996, 500, 16, 2, '2024-04-08', 312, 'Titular', 13, '1.5', 'Perdió todos los duelos aéreos y cuando debía aguantar de espalda tampoco decidió correctamente. En transiciones ofensivas, se le vio lento y con poco recurso para aprovechar las situaciones.');
