INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8067, 558, 16, 5, '2024-04-09', 29, 'Titular', 11, '3', 'partido plano desde lo individual pocas acciones de ataque por su banda, desde lo defensivo colaboro en las subidas del lateral y cerrar lineas de pase en progresión del rival');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8068, 558, 16, 5, '2024-04-09', 20, 'Titular', 13, '3.5', 'buen partido desde lo individual y lo colectivo buenas descargas para seguir una circulación y darle continuidad al juego. desde lo defensivo colabora en las presiones al rival y defiende la tactica fija');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8069, 558, 16, 5, '2024-04-09', 625, 'Titular', 14, '4', 'buen desempeño en ambas fases mucha recuperación de balón y da mucha continuidad al juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8070, 558, 16, 5, '2024-04-09', 274, 'Titular', 1, '3.5', 'Buena comunicación en fase defensiva ejerce su liderazgo ordenando a la línea defensiva, en fase ofensiva realiza transiciones rápidas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8071, 558, 16, 5, '2024-04-09', 44, 'Titular', 4, '4', 'buen rendimiento en fase defensiva no tuvo muchos problemas en la marcación a rival, en fase ofensiva se proyecta por su banda siempre generando ataques constantes.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8072, 558, 16, 5, '2024-04-09', 58, 'Titular', 12, '3', 'redimiento plano en fase ofensiva con un juego muy individual, en fase defensiva cierra líneas de pases por dentro marcando al volante y al extremo del rival en distintas situaciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8073, 558, 16, 5, '2024-04-09', 5, 'Titular', 10, '3.5', 'buen rendimiento en fase ofensiva le costo generar juego donde el rival presionaba mucho en medio campo, en fase defensiva genero muchas presiones donde forzaba el error al rival.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8074, 573, 16, 16, '2024-04-10', 627, 'Titular',null, '4', 'Muy buen desempeño, compitió de gran manera en los duelos ofensivos, sostuvo de buena manera y siempre entrego desahogo al equipo con sus descargas y desvíos, estuvo muy intenso en fase defensiva siendo nuestra primera línea de presión. Tuvo una ocasión clara de gol, pero definió de mala manera, estuvo poco fino en sus finiquitos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8075, 573, 16, 16, '2024-04-10', 46, 'Titular',null, '3', 'Buen partido, tuvo  minutos de buen rendimiento y alguno muy bajos, fue muy agresivo en los duelos, rápido en coberturas, nos entrega buenos inicios y rompe en conducción constantemente, los que nos genera romper presión rival, en este partido fue poco claro en su juego ofensivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8076, 573, 16, 16, '2024-04-10', 47, 'Titular',null, '4', 'Muy buen desempeño, uno de sus mejores partidos, muy agresivo e intenso en la fase defensiva, tuvo mucha claridad en nuestro juego ofensivo, oriento siempre el juego a zonas donde teníamos mejores opciones de progresar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8077, 573, 16, 16, '2024-04-10', 51, 'Titular',null, '3', 'Buen partido, tuvo un buen inicio en el juego, pero con el correr de los minutos fue bajando su rendimiento, poco agresivo en los duelos ofensivos, no fue desequilibrante en zona de finalización, en ocasiones ejecuto ese último pase (centro) de mala manera, pero tuvo un gran recorrido por banda, cumplió desde su posicionamiento siempre. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8078, 573, 16, 16, '2024-04-10', 54, 'Titular',null, '3', 'Buen partido, Héctor es un jugador con muchas capacidades individuales, en este partido estuvo más participativo en el juego, tuvo mayores intervenciones y mucha más movilidad, habilito constantemente a sus delanteros, pero sigue con muchas lagunas en el juego, esa es una tarea a mejorar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8079, 573, 16, 16, '2024-04-10', 55, 'Titular',null, '3', 'Buen partido, tuvo buenas intervenciones, nos entrego seguridad gran parte del juego, en algunas acciones tuvo malas ejecuciones, creo que tuvo un grado de culpabilidad en el gol rival, pero en líneas generales y considerando su accidente dentro del juego, Enzo nos entrego un buen desempeño.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8080, 573, 16, 16, '2024-04-10', 57, 'Titular',null, '4', 'Muy Buen Partido, Camilo, tuvo un gran desempeño defensivo, en gran parte del juego, ganos sus duelos, rápido en coberturas y muy posicional en su línea defensiva, nos entrego siempre buenos inicios y rompió en conducción para generar mayor volumen en zona media. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8081, 573, 16, 16, '2024-04-10', 76, 'Suplente ingresa',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8082, 573, 16, 16, '2024-04-10', 83, 'Titular',null, '3', 'Buen partido, tuvo grandes recorrido y muy dinámico en ambas fases del juego, pero desde lo técnico - táctico, no fue un partido sólido, tuvo algunos errores en controles y pases, Matías debe mejorar su control en el juego, tener mejor lectura y mejor manejo en el ritmo de juego. Tuvo una ocasión de gol, que no convierte por su ímpetu y apresuramiento en la ejecución.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8083, 573, 16, 16, '2024-04-10', 67, 'Titular',null, '3', 'Buen partido, cumplió dentro de sus tareas, tanto ofensivas como defensivas, tuvo un gran recorrido por banda en ambas fases del juego, debe seguir mejorando en ser un jugador mas desequilibrante en los ultima zona de juego, finalizar de mejor maneras sus acciones.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8084, 573, 16, 16, '2024-04-10', 68, 'Suplente ingresa',null, '3', 'Buen partido, entro pocos minutos, pero tuvo buenas intervenciones en fase ofensiva, saco buenos centros y finalizo bien sus acciones en la última zona.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8085, 573, 16, 16, '2024-04-10', 69, 'Suplente ingresa',null, '3', 'Buen partido, entro pocos minutos, pero tuvo buenas intervenciones y se vio muy seguro en sus acciones, muestra un gran potencial desde su biotipo y prestancia en el campo de juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8086, 573, 16, 16, '2024-04-10', 72, 'Titular',null, '5', 'Gran desempeño, Bastián tuvo un gran partido desde sus acciones, siempre fue alternativa de juego, resolvió de muy buena manera sus acciones, estuvo en constante movilidad para ser alternativa, tuvo una gran dinámica en nuestra fase defensiva, fue un jugador muy colaborador en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8087, 573, 16, 16, '2024-04-10', 61, 'Suplente ingresa',null, '3', 'Buen partido, entro para darnos mayor control del juego, que nos entregara una mejor progresión y ayudara al equipo en fase ofensiva, lo hizo muy bien, tuvo mucha movilidad y resolvió siempre de buena manera, le falto tener mayor profundidad en su juego y llegar a zona de finalización.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8088, 573, 16, 16, '2024-04-10', 656, 'Titular',null, '4', 'Muy buen partido, muy agresivo y rápido en duelos y coberturas, siempre estuvo bien posicionado nos entregó solides defensiva en nuestra última línea, tuvo una descoordinación que le entrego al rival la opción de convertir el primer gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8090, 532, 16, 14, '2024-04-12', 388, 'Titular', 2, '5', 'Sobresaliente partido de Vale, valorando de muy buena forma las ventajas profundas, desarticulando presiones con sus pases fuertes a espalda de las volantes. Se incorporó de forma sistemática al ataque, generando peligro constante por banda y teniendo excelendes asociaciones con Zamora.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8091, 532, 16, 14, '2024-04-12', 389, 'Titular', 4, '4.8', 'Muy destacado actuar de Nacha, pudo soltarse mas en fase ofensiva, se incorporó de forma permanente al ataque, generó doblajes por banda junto A Rebe, debe potenciar más esta faceta ya que dispone de excelente aceleración y tremenda capacidad de centro.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8092, 532, 16, 14, '2024-04-12', 390, 'Titular', 12, '4.5', 'Partido correcto de Rebe, desarticuló a la lateral de forma constante, recibió con ventaja en pasillos interiores, permitiendo ventajas a la Nacha por banda. Debe mejorar la última acción de remate, finalizar mejor las jugadas para ser mas contundente con todo el daño que produce rompiendo en conducción.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8093, 532, 16, 14, '2024-04-12', 391, 'Suplente ingresa', 14, '4.5', 'Karen suma muy buenos minutos, entregando mayor fluidez al juego asociado y mucha dinámica en recuperaciones. Debe atreverse a rematar más al arco cuando tiene ventaja espacial. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8094, 532, 16, 14, '2024-04-12', 392, 'Suplente ingresa', 4, '4', 'Los minutos que estuvo Montseta no fueron de mayor gravitancia ofensiva, defensivamente no tuvo ningún tipo de problema en defender la profundidad.Tuvo correctos pases en circulaciones para dar continuidad. Debe mejorar el aspecto físico para poder marcar mayor diferencia respecto a su competencia.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8095, 532, 16, 14, '2024-04-12', 393, 'Titular', 14, '4.5', 'Muy participativa en inicios de juego, valoró de muy buena forma los espacios a la espalda de laterales rivales. Tuvo excelentes intervenciones en pases profundos buscando a extremas. Salió por darle mas minutos a otras jugadoras. Otro buen partido de Llanka.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8096, 532, 16, 14, '2024-04-12', 397, 'Suplente ingresa', 5, '4.8', 'Excelentes minutos de Mariana, se pudo ver muy sólida en la última línea. Aportando en la zona de construcción, generando filtraciones de forma permanente, identificando las ventajas en espacios lejanos. Esto nos dió profundidad y llegar con mayor peligro al arco rival.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8097, 532, 16, 14, '2024-04-12', 398, 'Titular', 3, '4.7', 'Muy buena performance de Denisse, manejando los tiempos del partido. Tuvo gran capacidad de anticipación que le permitió ganar gran cantidad de duelos. Entendió muy bien los momentos de circulación y de progresión. Debe mejorar el juego aéreo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8098, 532, 16, 14, '2024-04-12', 400, 'Titular', 10, '5', 'Destacadísimo partido de Chama, utilizó muy bien la desventaja del rival posiciondose por detrás de la línea de medios. Muy participativa en la construcción del juego, cada vez mas entendiendo el juego de mejor forma jugando a toques necesarios para darle fluidez y continuidad al juego. Se sumó constantemente al área para generar volumen. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8099, 532, 16, 14, '2024-04-12', 401, 'Titular', 11, '4.3', 'Dani cumplió un correcto partido, dando línea de pase constante a la lateral de su lado. Además ejecutó desmarques profundos para generar espacio para su compañera en el espacio dejado o bien para recibir ella. Debe mejorar la definición en duelos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8100, 532, 16, 14, '2024-04-12', 403, 'Suplente ingresa', 12, '3.5', 'Chesca se mostró poco gravitante en el juego, un poco desconectada de los movimientos y patrones de movimiento que tiene en el equipo en fase ofensiva. Bastantes errores en ejecuciones de fundamentos básicos como controles y pases. Por otro lado, generó un par de acciones de peligro.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8101, 532, 16, 14, '2024-04-12', 410, 'Titular', 13, '4.7', 'Fer suma otra titularidad respondiendo de muy buena forma, garantizando la profundidad y sacando de referencias de forma permanente a las centrales. Dispone de una capacidad goleadora como pocas, esto le permite generarse acciones de peligro en espacios reducidos, tiene gran capacidad de remate, debe ajustar las ventajas que se generan en segunda línea cuando finaliza. Excelentes descargas de cara para dar continuidad, además de los muy buenos desmarques a la espalda de la línea defensiva. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8102, 532, 16, 14, '2024-04-12', 494, 'Titular', 3, '4.8', 'Coni es la defensora mas regular y destacada junto a Vale. Tiene gran lectura de anticipación, dispuso de su juego áereo para ganar gran cantidas de duelos. Muy criteriosa en inicios de juego distribuyendo el balón con mucha tranquilidad y fluidez. Lo único que falta es que saque mayor de voz de mando para tener mejor comunicación con sus línea defensiva y el equipo. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8103, 532, 16, 14, '2024-04-12', 507, 'Titular', 5, '5', 'Más que destacado partido de Gise, fundamental para poder generar progresión. Generó de forma sistemática intercambios con la línea de medios para tener ventajas espaciales. Jugadora muy inteligente que percibe y se mueve en espacios favorables de progresión. Generó rupturas con y sin balón que causaron muchísimo daño.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8104, 532, 16, 14, '2024-04-12', 620, 'Titular', 1, '5', 'Difcil evaluar a Abi ya que no tuvo intervenciones debido que el rival no llegó con peligro. Por otro lado todas sus intervenciones ofensivas en inicios de juego y continuidades fueron excelentes y con mucha seguridad.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8105, 532, 16, 14, '2024-04-12', 622, 'Suplente ingresa', 11, '4.8', 'Muy activa en fase ofensiva, cada vez con mas confianza, ganó gran cantidad de duelos ofensivos generando situaciones de gol. Meli va de menos a más en su rendimiento.');
