INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8190, 501, 16, 2, '2024-04-15', 305, 'Suplente ingresa', 12, '2.8', 'Entró con muy buena energía, se notó el impacto de él en el partido. Mucha personalidad para girar con balón e ir hacia delante.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8191, 501, 16, 2, '2024-04-15', 307, 'Titular', 12, '3', 'Siempre metido en el partido, jugador muy determinante en el último tercio. Metió el centro del gol de la U.');
