INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8193, 501, 16, 2, '2024-04-15', 317, 'Titular', 3, '2.5', 'No fue uno de sus mejores partidos. En juego asociado erró bastantes pases. Defensivamente tuvo un error de despeje en el gol.');
