INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8200, 501, 16, 2, '2024-04-15', 617, 'Titular', 13, '3.2', 'El mejor dle partido, aguantó bien de espalda, descargó y corrió al espacio. Defensivamente muy intenso, muy comprometido con el equipo.');
