INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8209, 559, 16, 5, '2024-04-15', 623, 'Titular', 2, '3.5', 'partido correcto desde la continuidad al juego, realiza desdoblamientos por banda mucha energia para generar situaciones, desde lo defensivo bien conectado con el partido recibiendo indicaciones de sus compañeros. ');
