INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8212, 559, 16, 5, '2024-04-15', 625, 'Titular', 9, '4.5', 'buen rendimiento desde lo individual hacia lo colectivo, mucha energia en la recuperación del balón desde la parte defensiva lo cual le permitio forzar un error y terminar en gol a favor nuestro. en ofensiva buena circulación de balón siempre con criterio para entregar un pase al compañero.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8213, 559, 16, 5, '2024-04-15', 44, 'Titular', 4, '4', 'buen partido siempre esta con disposición para colaborar donde se le requiere, en parte del segundo tiempo tuvo que jugar de stopper por izquierda logrando buenas intervenciones al modificar nuestro sistema de juego. da mucha continuidad al juego siempre progresa en ataque para generar superioridades por banda.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8214, 559, 16, 5, '2024-04-15', 58, 'Titular', 12, '4.5', 'Buen partido de ignacio si bien el primer tiempo no genero mucho por su banda con el cambio de sistema en el segundo tiempo quedo mas suelto y libre para poder atacar los espacios lo cual le permitio hacer gol, colabora en defensa en las presiones y salidas del rival.');
