INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8219, 501, 16, 20, '2024-04-16', 301, 'Suplente ingresa', 3, '1.5', 'Entro para sostener el resultado y falló en la jugada del gol rival.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8220, 501, 16, 20, '2024-04-16', 305, 'Suplente ingresa', 12, '2.5', 'Si bien entro prendido y con buenos duelos ofensivos, no cumplió con la tarea de mantener la posecion del balon y enfriar el partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8221, 501, 16, 20, '2024-04-16', 307, 'Titular', 12, '3.7', 'El jugador mas decisivo del equipo. Su prescencia en la cancha aporta juego, jerarquía y peligro. Aporto con asistencia. Le falto el gol.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8222, 501, 16, 20, '2024-04-16', 316, 'Suplente ingresa', 5, '3', 'Entro muy bien al partido, le entrego dinamica y continuidad al juego de la U. Físicamente se vio top.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8223, 501, 16, 20, '2024-04-16', 317, 'Titular', 3, '2.5', 'Partido ingrato. Muy regular defensivamente pero tuvo algunos errores inoportunos. En el gol del rival no puede dejar el balon vivo en el area.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8224, 501, 16, 20, '2024-04-16', 320, 'Titular', 14, '2', 'Intracendente en el juego. Se le exige ser más verticial y progresivo con los pases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8225, 501, 16, 20, '2024-04-16', 611, 'Titular', 11, '3.5', 'Buenos desmarques y duelos 1vs1. Muy generador de peligro. Le falto recibir mas balones en el ultimo tercio.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8226, 501, 16, 20, '2024-04-16', 612, 'Titular', 5, '2.5', 'Juego desmarcado y no logró encontrar a los media punta. Le aporto equilibrio al equipo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8227, 501, 16, 20, '2024-04-16', 613, 'Titular', 2, '4', 'El mejor del equipo. Mantiene su gran nivel en fase defensiva y en ataque. Es primordial en generar superioridades en 3/4.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8228, 501, 16, 20, '2024-04-16', 614, 'Titular', 3, '3.3', 'Muy regular y firme. Excelentes coberturas y mejora en los inicios con el balon. Le penaliza mucho el salto a destiempo en el gol en contra');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8229, 501, 16, 20, '2024-04-16', 615, 'Titular', 1, '3', 'Seguro, regular. Buen juego de pies. Debio salir a atacar la pelota en un centro.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8230, 501, 16, 20, '2024-04-16', 617, 'Titular', 13, '3.5', 'Aporto mucho peso ofensivo. Aguanto el balon y descargo para sumar gente en ataque. Gol y situación clara.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8231, 686, 16, 9, '2024-04-17', 648, 'Suplente ingresa', 11, '3', 'Renato ingresó en el complemento y los minutos que ingreso lo hizo bien, intento permanentemente desbordar y buscar el centro, quizás le falto más diagonales para buscar el mismo terminar la jugada. En labores defensivas estuvo bajo, debe colaborar mas en faceta defensiva.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8232, 686, 16, 9, '2024-04-17', 170, 'Titular', 1, '3', 'Lukas estuvo bien en general, sus intervenciones fueron seguras, y cuando se le exigio también estuvo bien al resolvar esas situaciones. Debe mejorar la interpretación del juego, le cuesta identificar cual es la mejor opción para la salida.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8233, 686, 16, 9, '2024-04-17', 176, 'Titular', 14, '2', 'Cristóbal es un muy buen jugador, sin embargo pese a que no hizo un mal partido si estuvo por debajo de su real rendimiento, Cristóbal debe seguir creciendo como jugador, en esta ocasion creo le paso la cuenta la lesión del partido frente Curicó, pese a que ya la superó me parece que con el partido de mitad de semana aun le persiste la molestia porque no estuvo en su mejor nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8234, 686, 16, 9, '2024-04-17', 164, 'Suplente ingresa', 14, '2', 'Cristian es un buen jugador sin embargo le cuesta aun sostener cuano el rival tiene jugadores más fuertes, con un biotipo mas grande. Pero él se las arregla para intentar resolver, en este partido sin hacer un mal partido el equipo se resintio en su funcionamiento porque perdiamos algunas opciones claras de avanzar porque él se demoraba en controlar o lo neutralizaban.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8235, 686, 16, 9, '2024-04-17', 167, 'Suplente ingresa', 12, '4', 'Leon fue el mejor de todos en este partido, pese a que jugó solo 25 minutos, su ingreso le dió al equipo posesión, velocidad y claridad en la salida, controló en el medio cada balon que pasaba por él y no permitió llegar con mayor claridad. Muy buen partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8236, 686, 16, 9, '2024-04-17', 166, 'Titular', 3, '3', 'David hizo un buen partido, no tuvo mucho trabajo defensivo, y en la salida siempre nos dio mucha claridad, el balón siempre sale bien jugado en los pies de David.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8237, 686, 16, 9, '2024-04-17', 171, 'Suplente ingresa', 14, '3', 'Felipe ingreso desde el inicio del segundo tiempo y se posicionó un poco mas atras de donde juega regularmente y nos permitio salir mejor desde atrás, eso y porque antes del teemino del primer tiempo sufrimos la expulsion de Valenzuela. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8238, 686, 16, 9, '2024-04-17', 183, 'Titular', 3, '3', 'Al igual que David, Franco no fue exigido pero sus intervenciones fueron acertadas en lo defensivo. Si le pedí que saliera jugando, que permitiera que le salieran a presionar y desde ahi generar una superioridad en el medio campo para llegar mejor a zona de finalización. Cumplio bien esa tarea.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8239, 686, 16, 9, '2024-04-17', 649, 'Titular', 11, '4', 'Dylan es un delantero muy veloz y muy fuerte fisicamente, su arranques desde la banda por lo general terminan en peligro. Si debe mejorrar sus decisiones cuando esta sobre el area, ahi se tiende a confundir un poco ya que busca rematar desde ahi cuando hay veces que puede avanzar más y en otras avanza en vez de rematar. Pero en general muy buen partido.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8240, 686, 16, 9, '2024-04-17', 161, 'Suplente ingresa', 2, '3', 'Benjamin juego desde el inicio del segundo tiempo y no tenia una tarea facil ya que el extremo reival era el mas peligroso por su velocidad, sien embargo Benjamin lo controló muy bien y además sus desdoblamientos por su banda nos permitio tener mas equilibrio en esa faceta, ya que por izquierda lo tenemos con Neira. Buen partido de Baltra.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8241, 686, 16, 9, '2024-04-17', 162, 'Titular', 13, '3', 'Santiago hizo un buen partido, siempre muy genereso con el equipo en las labores defensivas, ayuda permanentemente en la presión sin embargo aun debe aprender a moverse mejor cuando el equipo va en fase ofensiva o en transicion ofensiva ya que en ocasiones no da las alternativas correctas. Pero siempre esta muy dispuesto a colaborar con el equipo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8242, 686, 16, 9, '2024-04-17', 180, 'Titular', 5, '2', 'En mi opinion este partido fue el mas bajo de Alonso, la cancha era mas chica pero le quedaba bien a esta categoría, sin embargo Alonso no estuvo acertado en las decisiones cuando recibia el balon se enredo siempre, no le dio claridad al equipo, en otros momentos si se vio bien pero lo genral es que no lo vi comodo. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8243, 686, 16, 9, '2024-04-17', 182, 'Titular', 4, '3', 'Esteban cumplio con su rendimiento habitual, bien en lo defensivo, criterioso cuando va en funciones ofensivas. Es salida clara por ese sector y permite que el equipo tenga llegada desde atras en esa cobertura ofensiva con su estilo de juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8244, 686, 16, 9, '2024-04-17', 178, 'Titular', 2, '2', 'Matias tuvo el partido mas bajo en lo que llevamos, (dejo afuera el partido anterior con la UC porque ahi todo el equipo estuvo bajo), le costo controlar al delantero y nos da poca llegada arriba en esos desdobles que el equipo necesita, le cuesta a parte ofensiva a Matias.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8245, 686, 16, 9, '2024-04-17', 184, 'Titular', 11, '3', 'Nicolas es un buen extremo derecho, es fuerte y bueno en el uno contra uno, sin embargo tiende a realizar lo mismo siempre y para el defensa tienede a neutralizarlo pero como es habil por lo general resuelve. Hoy por expulsion de un compañero lo ubique mas al medio y un poco retrasado y realizo un buen partido mientras estuvo en esa posicion.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8246, 604, 16, 17, '2024-04-17', 126, 'Suplente ingresa', 13, '3', 'Juego un partido correcto aportando mucho sacrificio y movilidad en ataque, aunque creo que al momento de la disputa deensiva todavia esta al debe, sin embargo aporto lo que se esperaba de el.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8247, 604, 16, 17, '2024-04-17', 119, 'Titular', 5, '4.1', 'Jugo un buen partido, destrabando nuestro juego moviendo el balon de un lado hacia el otro, el peso corporal todavia no le ayuda a desarrollar su mejor version, pero es un jugador con mucho caracter que le da al equipo el espiritu necesario para competir. Bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8248, 604, 16, 17, '2024-04-17', 124, 'Titular', 4, '2.8', 'tuvo un partido con altos y bajos, ya que participa activamente en el primer gol nuestro, pero si creo que al momento de pasar a defender es donde el necesita crecer y poner mas de su parte. Bajo nivel, se esperaba mas!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8249, 604, 16, 17, '2024-04-17', 127, 'Titular', 13, '3.3', 'Jugo un partido bajo desde lo fisico y tecnico aunque desde lo tactico le aporta al equipo mucho. No fue su mejor paertido pro tampoco el peor. EN resumidas cuenta cumplio con lo esperado!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8250, 604, 16, 17, '2024-04-17', 122, 'Titular', 12, '4.2', 'Jugador que cumple con todo lo que se le pide, aunque asi y todo no ha llegado a mostrar su maximo potenicla. Un ejemplo de jugador quien se entrega por el equipo a cabalidad e imprime un espiritu y fuerza en lo colectivo que hace que el equipo tenga la competitividad necesaria para seguir luchando. Bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8251, 604, 16, 17, '2024-04-17', 108, 'Titular', 1, '4.2', 'Jugo un muy buen partido, entregandole al equipo seguridad y prestancia con el balon y tambien al momento de atajar. Un lider del equipo!. Bien');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8252, 604, 16, 17, '2024-04-17', 113, 'Titular', 3, '2.8', 'Jugo un partido muy bajo desde lo defensivo como en lo ofensivo, cayendo  en lagunas de desconcentracion que al final comienzan a perjudicar al equipo en el colectivo. Asi y todo su juego aerero defensivo ayuda al equipo en ese sentido...mucho que mejorar!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8253, 604, 16, 17, '2024-04-17', 116, 'Titular', 3, '4.4', null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8254, 604, 16, 17, '2024-04-17', 131, 'Titular', 10, '4.4', 'Jugo un gran partido desde todas las fases del juego...prestando ayudas defensivas y ofensivas a cada momento que el equipo lo requirio. En estos momentos tiene un nivel superlativo que lo hace un jugador proyectable. Muy bien!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8255, 604, 16, 17, '2024-04-17', 114, 'Suplente ingresa', 3, '3.3', 'Hizo un buen partido, muy concentrado desde lo defensivo como lo ofensivo aunque tuvo incidencia directa en el gol rival. Cumplio!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8256, 604, 16, 17, '2024-04-17', 120, 'Suplente ingresa', 6, '2.3', 'Jugo muy poco, pero en cada momento que le toco intervenir se le vio nervioso y duditativo perdiendo duelos y el balon en salida...muy bajo!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8257, 604, 16, 17, '2024-04-17', 111, 'Titular', 2, '3', 'Jugo un partido muy por debajo del nivel que habia mostrado en partidos anteriores...desde la perspectiva del plan de juego, el sabia que seria un agente importante en la distribucion del balon, pero le costo muchisimo sobre llevar ese rol ya que necesita mejorar mucho en el aspecto tecnico. Bajo!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8258, 604, 16, 17, '2024-04-17', 130, 'Titular', 11, '3.9', 'Jugo un buen partido desde la entrega y en lo que concierne a su juego con balon. Aunque cumplio con el rendimiento indiviual en esos aspectos, esta todavia al debe en lo que concierne a su juego sin balon al momento de atacar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8259, 604, 16, 17, '2024-04-17', 123, 'Titular', 14, '2.5', 'Jugo un juego muy bajo desde la perspectiva ofensiva, llegando tarde o muy temprano a lugares para recibir el balon con ventaja, a su vez al momento de la distribucion del balon es donde esta un segundo mas tarde de lo que el partido lo requiere. En lo defensivo tiene mucho sacrificio y voluntad para ayudar, aunque todavia le cuesta ganar los duelos defensivos. Bajo!');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8260, 501, 16, 3, '2024-04-17', 633, null,null, null, null);
