INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8262, 501, 16, 3, '2024-04-17', 280, 'Titular', 4, '2.8', 'Regular. No fue el aporte ofensivo que venía siendo, pero no tuvo tantos problemas con Barrera y Escobar');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8263, 501, 16, 3, '2024-04-17', 301, 'Suplente ingresa', 3, '1.8', 'Aunque jugó pocos minutos para ser evaluado, tuvo mal timming en el gol');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8264, 501, 16, 3, '2024-04-17', 305, 'Suplente ingresa', 12, '2.9', 'Intentó encarar, tuvo personalidad para eso, pero perdió dos pelotas innecesarias');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8265, 501, 16, 3, '2024-04-17', 307, 'Titular', 12, '3.4', 'Sacó el centro para gol, se avivó en un tiro libre que se debiese haber cobrado, siempre intentó, fue mas geneorso, aunque se cansó');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8266, 501, 16, 3, '2024-04-17', 316, 'Suplente ingresa', 5, '3.3', 'Jugó bien todas bien, pero pudo haber sido aun mas protagonista');
