INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8268, 501, 16, 3, '2024-04-17', 320, 'Titular', 14, '2.3', 'Gran recorrido, pero controló mucho para atrás y no le dio profundidad al equipo');
