INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8275, 574, 16, 16, '2024-04-17', 627, 'Titular', 13, '5', 'Gran desempeño, tuvo un partido con gran rendimiento, sostuvo y descargo de muy buena manera, en fase ofensiva estuvo siempre bien ubicado y muy fuerte en duelos ofensivos, en fase defensiva, tuvo un gran desgaste, siempre fue nuestra primera línea de presión, convirtió un gol y una habilitación.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8276, 574, 16, 16, '2024-04-17', 33, 'Suplente ingresa', 3, '3', 'Buen desempeño, entro para entregarnos seguridad defensiva, teníamos un jugador menos y Diego ingreso para darnos mayor juego aéreo en defensa tras centros del rival, estuvo bien y gano la mayoría de duelos aéreos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8277, 574, 16, 16, '2024-04-17', 39, 'Suplente ingresa', 5, '3', 'Buen desempeño, Vicente entro con funciones claras en la generación de juego, volver a tener el control de el, utilizar espacios libres, y tener un buen equilibrio defensivo, al quedar con un jugador menos, lo realizo de buena manera, pero debe seguir mejorando sus controles y su perfil menos hábill.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8278, 574, 16, 16, '2024-04-17', 46, 'Titular', 3, '3', 'Buen desempeño, Diego en muchas acciones de juego realizo las tareas asignadas, entregar buenos inicios, romper en conducción para sumar mas jugadores en zona de generación, jugar en amplitud y cerrar su zona en fase defensiva, cuando Diego, es concentrado y es simple en su juego, no tiene problemas para tener un buen desempeño, pero en este partido estuvo displicente en algunas acciones y cometío muchos errores en ambas fases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8279, 574, 16, 16, '2024-04-17', 47, 'Titular', 5, '5', 'Gran desempeño, Rubén cada vez esta más solido en su juego, cuando Rubén es simple y se posiciona de buena manera en zona media, siempre entrega buenos inicios y una generación más limpia, esta orientando constantemente el juego a lados favorables para tener superioridad numérica, su característica principal, es ser un jugador muy intenso en ambas fases, lo que mantiene y a sumado mayor agresividad en duelos disputados.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8280, 574, 16, 16, '2024-04-17', 51, 'Titular', 6, '4', 'Muy Buen desempeño, Franco tuvo un  recorrido constante por  banda, buen despliegue  en ambas fases, finaliza y aparece como segunda línea de ataque muy bien, pero aún debe ser mas solido su juego, mas agresivo en ultimo tercio cuando juega duelos ofensivos y seguir mejorando aspectos defensivos que hoy involucra su posición.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8281, 574, 16, 16, '2024-04-17', 57, 'Titular', 3, '4', 'Muy Buen desempeño, cada vez su juego es mas solido desde lo defensivo y mejorando mucho en lo ofensivo, tuvo un partido correcto en ambas fases del juego, muy agresivo en los duelos disputados y entrego siempre inicios claros, debe mejorar en su ubicación y mirar a su alrededor, ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8282, 574, 16, 16, '2024-04-17', 76, 'Titular', 13, '4', 'Muy Buen partido, su nota está condicionada por su expulsión, la cual, podríamos decir que fue o no injusta, pero en la realidad nos dejó con un jugador menos, esto sin duda lleva a buscar como ese jugador menos no nos perjudique, pero Andrés en lo netamente deportivo y relacionado con su desempeño deportivo, fue un gran partido, convirtió 3 goles y estuvo siempre conectado con el juego, buenas descargas y muy bien en las asociaciones ofensivas, ayuda en nuestra primera línea de presión, algo que debe seguir en la mejora, al ser un jugador poco intenso y agresivo.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8283, 574, 16, 16, '2024-04-17', 83, 'Suplente ingresa', 5, '3', 'Buen partido, Matías entro para entregarnos un gran despliegue en zona media al tener un jugador menos, que con su intensidad que ayudara a cubrir espacios mas amplios y que en fase ofensiva nos entregara movilidad para tener control del juego, que ocupara de buena manera los espacios libres, logro en muchos pasaje realizar las tareas asignadas, pero su ímpetu, lo lleva en muchas ocasiones a cometer muchos errores ofensivos desde pases y controles, algo que debe mejorar, eso lo ayudara en su juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8284, 574, 16, 16, '2024-04-17', 67, 'Titular', null, '4', 'Muy Buen partido, Matías tuvo un gran despliegue por banda, cumple muy bien las tareas asignadas en ambas fases del juego, es un jugador muy táctico, siempre estuvo bien posicionado. Pero debe ser mas desequilibrante en fase ofensiva, tener mayor agresividad en duelos ofensivos, pisar el área rival con mayor convicción, en fase defensiva cumple sus tareas de buena manera.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8285, 574, 16, 16, '2024-04-17', 68, 'Suplente ingresa', null, '3', 'Buen partido, Benjamín entro para darnos un nuevo aire por banda izquierda, que con su velocidad, nos posicionáramos en campo rival con balones mas al espacio, que lograra romper y darnos amplitud en el juego, en muchas jugadas logro entregar lo solicitado, pero su técnica individual lo hace cometer errores en controles y pases, su mala toma de decisiones lo llevo a cometer errores de lectura en el juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8286, 574, 16, 16, '2024-04-17', 69, 'Titular', 1, '4', 'Muy Buen partido, tuvo buenas intervenciones y nos entrega solides en el juego aéreo, entrego buenos inicios y respondió a ser un jugador que se espera mucho en su desarrollo, debe seguir en la mejora de sus tomas de decisiones en el juego y mas personalidad en entregar instrucciones a sus defensas y equipo.');
