INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8481, 575, 16, 16, '2024-04-24', 627, 'Titular', 13, '4', 'Muy buen desempeño, fue nuestro referente en ataque, sostuvo y compitió con centrales rivales, tuvo mucha movilidad y siempre fue opción para tener un desahogo, fue poco asistido en ataque, a pesar, logro convertir 2 goles, debe mejorar en fase defensiva, específicamente en balones detenidos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8482, 575, 16, 16, '2024-04-24', 47, 'Titular', 5, '4', 'Muy buen desempeño, Rubén tuvo un partido muy equilibrado en ambas fases, mucha agresividad e intensidad en los duelos y fase defensiva, en fase ofensiva, cada vez entrega una mejor progresión en el juego y fue muy simple en su juego.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8483, 575, 16, 16, '2024-04-24', 51, 'Titular', 6, '2', 'Regular desempeño, Franco, tuvo regresos muy lentos y no mantuvo la intensidad en el juego por banda, poco agresivo en los duelos, ofensivos y defensivos, no cubrio y realizo coberturas a espalda de su central, no tuvo trascendencia en el juego ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8484, 575, 16, 16, '2024-04-24', 54, 'Titular', 10, '3', 'Buen partido, Héctor, tuvo buenos pasajes en el juego, se asoció en zonas de progresión y finalización,  fuerte en duelos ofensivos, tiene un maneio de ambos perfiles lo que le  ayuda a ser un jugador poco predecible. Su gran problemática que  sigue pasando por momentos muy erráticos, con mala toma de decisiones en el juego, pero algo mas preocupante, su intensidad en el juego, en ambas fases.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8485, 575, 16, 16, '2024-04-24', 57, 'Titular', 3, '2', 'Regular desempeño, Camilo, estuvo muy errático en los inicios, tomando malas decisiones en los pases, estuvo muy bajo en los duelos defensivos, muy errático y distantes en los duelos aéreos y mala ubicación en zona defensiva, bajo mucho el rendimiento mostrado en partidos anteriores.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8486, 575, 16, 16, '2024-04-24', 79, 'Titular', 3, '3', 'Buen partido, Agustín, entrego buenos inicios, fue alternativa de pases para seguir en la rotación y decidió bien en jugar corto o largo, estuvo bien ubicado en la mayoría de las acciones de juego, gano muchos duelos en velocidad. Agustín debe mejorar en su carácter transferido al juego, era su primer partido y se nota en algunos pasajes sus temores al juego, pero tiene un potencial a trabajar.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8487, 575, 16, 16, '2024-04-24', 83, 'Suplente ingresa', 14, '3', 'Buen partido, Matías, nos entregó intensidad y agresividad en zona media, sobre todo en fase defensiva. Mucha dinámica en el juego, estuvo poco errático en su intervenciones, en la generación de juego fue una alternativa clara para asociarnos y profundizar, tuvo recorridos largos y estuvo en ambas áreas.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8488, 575, 16, 16, '2024-04-24', 67, 'Titular', null, '3', 'Buen partido, Matías, tuvo grandes recorridos por banda, fue siempre amplitud en el juego, tomo mejores decisiones en el juego. Matías debe mejorar su agresividad en el juego, en ambas fases, muy pasivo en profundidad y en zona defensiva, lo que hace que sea un jugador lineal en su juego, se esta siempre esperando algo mas en su rendimiento');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8489, 575, 16, 16, '2024-04-24', 68, 'Suplente ingresa', 4, '3', 'Buen partido, Benjamín, entro para darnos mayor dinámica en banda, con su ingreso y el resultado en contra, se posiciono como lateral izquierdo, culplió y cerro su zona, mejoro en duelos defensivos, en fase ofensiva se proyectó a zonas de progresión y finalización, con mayor claridad, nos entrego amplitud.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8490, 575, 16, 16, '2024-04-24', 69, 'Titular', 1, '3', 'Buen partido, José, tuvo buenas  intervenciones, entrego seguridad a pesar de acciones erráticas en el juego, en los inicios mejoro mucho y tomo mejores decisiones. José, debe seguir creciendo y buscando mejorar en los errores cometidos, que son parte de su desarrollo. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8491, 575, 16, 16, '2024-04-24', 72, 'Titular', 10, '3', 'Buen partido, Bastián, tuvo buenas intervenciones en el juego, cuando fue simple, encontró espacios y tuvo movilidad, nos entregó una buena progresión y finalización en el juego. Bastián, debe mejorar su simpleza en el juego, entender zonas donde su juego es mas individual y seguir en la mejora de su intensidad.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8492, 575, 16, 16, '2024-04-24', 77, 'Titular', 14, '4', 'Muy buen desempeño, Elías, entrego intensidad y agresividad en el juego, rompe siempre a los espacios libres, tiene buenos controles, que le permitieron romper líneas de presión del rival, se suma de muy buena manera en la progresión y finalización del equipo, mucha movilidad en el juego, es siempre alternativa de pase. Elías, es uno de los puntos altos en la mayoría de los partidos disputados, tiene muchas aptitudes individuales, que le permiten jugar siempre a un buen nivel.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8493, 575, 16, 16, '2024-04-24', 61, 'Suplente ingresa', 11, '3', 'Buen partido, Cristóbal, entro para darnos un mejor juego en la progresión, con su ingreso volvimos a tener mayor control del juego, tuvo mucha movilidad y fue siempre alternativa de juego, su dificultad esta en fase defensiva, por su biotipo y por su compromiso a enfrentar duelos.');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (8494, 575, 16, 16, '2024-04-24', 656, 'Titular', 3, '4', 'Muy buen desempeño, Cristóbal, entrego solidez en su zona, muy agresivo en duelos y rápido en las coberturas, estuvo siempre bien posicionado. Debe mejorar en la velocidad de rotación en los inicios, atreverse a entrar en conducción y romper con pases entre líneas.');
