INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4765, 175, 11, 6, '2023-07-15', 115, 'Titular',null, '4', 'Buen partido de nico, comunicando, corrigiendo errores de los volantes. Sus inicios fueron largos buscando al delantero centro. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4766, 175, 11, 6, '2023-07-15', 116, 'Titular',null, '4', 'Mejoro mucho este partido con respecto al del primer semestre. Tuvo actitud y caracter para ganar tanto sus duelos ofensivos y defensivos. se proyecto bastante en ataque, llegando a finlizar acciones de remate ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4767, 175, 11, 6, '2023-07-15', 117, 'Suplente ingresa',null, '2', 'Bajo nivel, estuvo muy nervioso durante el partido. Tuvo errores de marca y de posicion al momento de defender. Sobre el final su toma de decisiones no fue la mejor incluso decir bastante errada, dando mucho pase al rival ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4768, 175, 11, 6, '2023-07-15', 118, 'Titular',null, null, null);
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4769, 175, 11, 6, '2023-07-15', 119, 'Suplente ingresa',null, '4', 'Buen partido, agresivo, en la marca, ganando juego aereo y duelos. Ofensivamente buscaba jugar con el equipo pero tenia 3 jugadores presionandolo constantemente. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4770, 175, 11, 6, '2023-07-15', 120, 'No citado',null, '2', 'Si bien es un jugador muy agresivo al momento de defender, es un jugador que debe correr para defender un espacio, no correr por correr o correr dentras del balon. Se perdio en el campo de juego ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4771, 175, 11, 6, '2023-07-15', 122, 'Titular',null, '4', 'Cuando no entra de titular se nota su falta de juego. Junto con marinetti, hace que el equipo tenga movilidad en zona media. Debe jugar mas rapido con el balon. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4772, 175, 11, 6, '2023-07-15', 121, 'Titular',null, '3', 'No gravita en el juego. Debe seguir buscando su espacio dentro del campo de juego. Su juego es muy lento. Realiza bastante enganches para poder finalizar la jugada. Defebnsivamente muy bajo para recuperar el balon. ');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4773, 175, 11, 6, '2023-07-15', 123, 'Lesionado',null, '5', 'Sin duda destaca por su posicion en el campo y su movilidad. Siempre esta buscando un espacio para poder jugar, generar lineas de pases. Defensivamente es un jugador agresivo, dificil de poder sacarse la marca ');
