INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4775, 175, 11, 6, '2023-07-15', 125, 'Lesionado',null, '3', 'No fue su mejor partido. Se le vio algo ansioso con balon. Pero es un jugador que marca diferencia. Genera una o dos acciones de gol por partido, siendo determinante en su juego');
INSERT INTO evaluacion_partido (id_evaluacion, partido_id, categoria_id, evaluador_id, fecha_evaluacion, jugador_id, condicion, posicion_id, nota, comentario)             VALUES (4776, 175, 11, 6, '2023-07-15', 126, 'Titular',null, '4', 'Buen partido, debe mejorar su finiquito, se genera ocasiones desde la amplitud maxima. Defensivamente es un jugador muy colaborativo ');
